"use client"

import { LucideIcon, Box, Shield, Leaf, Zap, Heart,Diamond , Star,ShoppingCart , Flag} from "lucide-react"

/**
 * Value Item Interface
 */
interface ValueItem {
  iconName: string
  title: string
  description: string
}

/**
 * Values Section Props
 */
interface ValuesSectionProps {
  sectionTitle: string
  sectionSubtitle: string
  values: ValueItem[]
  headerIcon?: string
  className?: string
  headingTitle?: string
}

/**
 * Values Section Component
 *
 * A fully dynamic and reusable component for displaying company values or features.
 *
 * Features:
 * - Customizable section title and subtitle
 * - Dynamic value items with icons, titles, and descriptions
 * - Responsive three-column layout
 * - Dark and light theme support
 * - Clean typography and spacing
 * - Reusable across different sections
 */
export function ValuesSection({
  sectionTitle,
  sectionSubtitle,
  headingTitle,
  values,
  headerIcon,
  className = ""
}: ValuesSectionProps) {
  // Icon mapping
  const iconMap: Record<string, LucideIcon> = {
    Box,
    Shield,
    Leaf,
    Star,
    Zap,
    Heart,
    Diamond,
    ShoppingCart,
    Flag
  }

  // Helper function to get icon component
  const getIcon = (iconName: string): LucideIcon => {
    return iconMap[iconName] || Box
  }
  return (
    <section className={`py-12 sm:py-16 md:py-20 px-4 sm:px-6 lg:px-8 ${className}`}>
      <div className="max-w-7xl mx-auto">
        {/* Section Header */}
        <div className="mb-12 sm:mb-16">
          {/* Badge */}
          <div className="mb-8 sm:mb-12">
            <div className="flex items-center border-[0.5px] border-[#25252518] dark:border-[#fafafa38] rounded-full px-4 py-3 gap-3 max-w-fit">
              {headerIcon && (
                <div className="flex items-center justify-center">
                  {(() => {
                    const HeaderIconComponent = getIcon(headerIcon)
                    return (
                      <HeaderIconComponent
                        className="w-5 h-5 sm:w-6 sm:h-6 text-[#252525] dark:text-[#FAFAFA]"
                        strokeWidth={1.5}
                      />
                    )
                  })()}
                </div>
              )}
              <span className="text-[#252525] text-lg sm:text-xl dark:text-[#FAFAFA] font-medium">
                {sectionTitle}
              </span>
            </div>
          </div>

          {/* Main Heading and Description */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-12 items-start">
            {/* Main Heading */}
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] text-[#252525] dark:text-[#FAFAFA] leading-tight">
              {headingTitle}
            </h2>

            {/* Section Description */}
            <div className="flex items-start lg:items-center">
              <p className="text-xl lg:text-[24px] text-[#252525] lg:text-right dark:text-[#FAFAFA] leading-relaxed">
                {sectionSubtitle}
              </p>
            </div>
          </div>
        </div>

        {/* Values Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8">
          {values.map((value, index) => {
            const IconComponent = getIcon(value.iconName)
            return (
              <div key={index} className="space-y-6 p-8 md:p-9 bg-[#FBFBFB] dark:bg-[#2A2A2A] rounded-2xl sm:rounded-3xl">
                {/* Icon */}
                <div className="w-12 h-12 sm:w-14 sm:h-14 rounded-full border border-[#2525255f] dark:border-[#fafafa28] mb-4 sm:mb-6 flex items-center justify-center">
                  <IconComponent
                    className="w-5 h-5 sm:w-6 sm:h-6 text-[#252525] dark:text-[#FAFAFA]"
                  />
                </div>

                {/* Title */}
                <h3 className="text-[28px] md:text-3xl text-[#252525] font-medium dark:text-[#FAFAFA]">
                  {value.title}
                </h3>

                {/* Description */}
                <p className="text-xl text-[#252525] dark:text-[#FAFAFA] leading-relaxed">
                  {value.description}
                </p>
              </div>
            )
          })}
        </div>
      </div>
    </section>
  )
}

"use client";

import { useState, useEffect } from "react";
import Image from "next/image";
import { MessageSquare } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export function GallerySection() {
  const [activeImage, setActiveImage] = useState(0);
  const [hoveredThumbnail, setHoveredThumbnail] = useState<number | null>(null);
  const [direction, setDirection] = useState(0);
  const [animationDuration] = useState(0.6);
  const [isDesktop, setIsDesktop] = useState(false);

  // Handle responsive spacing
  useEffect(() => {
    const checkScreenSize = () => {
      setIsDesktop(window.innerWidth >= 1024);
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);

    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const handleImageChange = (newIndex: number) => {
    if (newIndex === activeImage) return;
    
    // Calculate distance between current and target image
    const distance = Math.abs(newIndex - activeImage);
    
    // Set direction: 1 for forward (down to up), -1 for backward (up to down)
    setDirection(newIndex > activeImage ? 1 : -1);
    setActiveImage(newIndex);
    
    // Return distance for dynamic duration
    return distance;
  };

  const galleryImages = [
    {
      id: 1,
      src: "/gallery-1.webp",
      alt: "Modern Black Speaker",
    },
    {
      id: 2,
      src: "/gallery-2.webp",
      alt: "Premium Audio System",
    },
    {
      id: 3,
      src: "/gallery-3.webp",
      alt: "Wireless Speaker Setup",
    },
    {
      id: 4,
      src: "/blog-1.webp",
      alt: "Studio Monitor",
    },
    {
      id: 5,
      src: "/blog-5.webp",
      alt: "Compact Speaker Design",
    },
  ];

  // Animation variants for vertical sliding
  const slideVariants = {
    enter: (direction: number) => ({
      y: direction > 0 ? '100%' : '-100%',
      opacity: 1,
    }),
    center: {
      y: 0,
      opacity: 1,
    },
    exit: (direction: number) => ({
      y: direction > 0 ? '-100%' : '100%',
      opacity: 1,
    }),
  };

  return (
    <section className="py-12 sm:py-16 md:py-20 lg:py-24 px-4 sm:px-6 md:px-8">
      <div className="max-w-7xl mx-auto">

        {/* Header Section */}
        <div className="mb-8 sm:mb-10 md:mb-12">
          {/* Badge */}
          <div className="flex border-[0.5px] border-[#2525252f] dark:border-[#fafafa4b] rounded-full px-4 py-2 w-fit items-center justify-center gap-2 mb-6 sm:mb-8">
            <MessageSquare className="w-4 h-4 sm:w-5 sm:h-5 text-[#252525] dark:text-[#fafafa]" />
            <span className="text-base sm:text-lg md:text-[20px] text-[#252525] dark:text-[#fafafa]">
              In reality
            </span>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-12 justify-between items-start lg:items-center">
            {/* Main Title */}
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] text-[#252525] dark:text-[#fafafa] leading-tight">
              Timeless Elegance
            </h2>

            {/* Description */}
            <p className="text-[20px] text-[#252525] lg:text-right dark:text-[#fafafa] leading-tight">
              Enhance your space with a perfect blend of modern design and warm ambiance. Our speaker brings style and sophistication
            </p>
          </div>
        </div>

        {/* Gallery Layout */}
        <div className="relative h-[300px] sm:h-[400px] md:h-[500px] lg:h-[600px]">

          {/* Main Image Container */}
          <div className="relative w-full h-full rounded-2xl sm:rounded-3xl overflow-hidden bg-gray-100 dark:bg-gray-800">

            {/* Animated Images */}
            <AnimatePresence initial={false} custom={direction}>
              <motion.div
                key={activeImage}
                custom={direction}
                variants={slideVariants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  type: "tween",
                  ease: [0.25, 0.1, 0.25, 1],
                  duration: 0.6
                }}
                className="absolute inset-0"
              >
                <Image
                  src={galleryImages[activeImage].src}
                  alt={galleryImages[activeImage].alt}
                  fill
                  className="object-cover"
                  priority
                />
              </motion.div>
            </AnimatePresence>

            {/* Thumbnail Overlay */}
            <div className="absolute left-3 sm:left-4 md:left-6 top-1/2 transform -translate-y-1/2 flex flex-col gap-2 sm:gap-3 z-20">

              {/* Animated Ring that follows hover */}
              <motion.div
                className="absolute ring-1 p-2 sm:p-3 ring-white shadow-lg pointer-events-none w-[50px] h-[35px] sm:w-[60px] sm:h-[40px] rounded-[50px]"
                animate={{
                  y: (hoveredThumbnail !== null ? hoveredThumbnail : activeImage) * (isDesktop ? 52 : 43), // 43 on mobile, 52 on desktop
                }}
                transition={{
                  type: "spring",
                  stiffness: 400,
                  damping: 30,
                  mass: 0.8
                }}
              />

              {galleryImages.map((image, index) => (
                <motion.div
                  key={image.id}
                  className="relative cursor-pointer overflow-hidden w-[50px] h-[35px] sm:w-[60px] sm:h-[40px] rounded-[50px]"
                  whileHover={{
                    scale: 1,
                    transition: { duration: 0.2 }
                  }}
                  whileTap={{ scale: 0.95 }}
                  animate={{
                    scale: hoveredThumbnail === index ? 1.2 :
                           hoveredThumbnail !== null ? 1.1 : 1.0
                  }}
                  transition={{ duration: 0.3, ease: "easeOut" }}
                  onClick={() => handleImageChange(index)}
                  onMouseEnter={() => setHoveredThumbnail(index)}
                  onMouseLeave={() => setHoveredThumbnail(null)}
                >
                  <Image
                    src={image.src}
                    alt={image.alt}
                    fill
                    className="object-cover"
                  />

                  {/* Active indicator overlay - only show on actual active image */}
                  {activeImage === index && hoveredThumbnail === null && (
                    <motion.div
                      className="absolute inset-0 bg-white/10 backdrop-blur-[1px] rounded-[50px]"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    />
                  )}


                </motion.div>
              ))}
            </div>

            {/* Overlay gradient for better contrast */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300 z-10" />
          </div>

        </div>

      </div>
    </section>
  );
}

"use client";

import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "../ui/accordion";

export function FAQSection() {
  const faqs = [
    {
      id: "item-1",
      question: "What makes your speakers stand out?",
      answer: "Our speakers deliver premium sound quality, sleek design, and long-lasting durability, ensuring an unmatched listening experience.",
      defaultOpen: true,
    },
    {
      id: "item-2",
      question: "Are your speakers compatible with all devices?",
      answer: "Yes, our speakers support Bluetooth 5.0, AUX input, and USB-C connectivity, making them compatible with virtually all modern devices including smartphones, tablets, laptops, and smart TVs.",
    },
    {
      id: "item-3",
      question: "How long does the battery last?",
      answer: "Our speakers provide up to 12 hours of continuous playback at 70% volume. Battery life may vary depending on volume level, audio content, and environmental conditions.",
    },
    {
      id: "item-4",
      question: "Are the speakers waterproof?",
      answer: "Our speakers feature IPX5 water resistance rating, making them splash-proof and suitable for outdoor use. They can withstand light rain and splashes but should not be fully submerged in water.",
    },
    {
      id: "item-5",
      question: "Do you offer a warranty?",
      answer: "Yes, we provide a comprehensive 2-year warranty covering manufacturing defects and hardware failures. Our warranty includes free repair or replacement and dedicated customer support.",
    },
  ];

  return (
    <section className="py-10 sm:py-12 md:py-14 px-4 sm:px-6 md:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="grid lg:grid-cols-2 gap-8 lg:gap-12 items-start">
          {/* Left Column - Header */}
          <div className="space-y-2 sm:space-y-8">
            {/* Badge */}
            <div className="flex items-center gap-3 text-[#252525] dark:text-[#fafafa] w-fit py-3">
              <span className="text-[28px]">Welcome to Beyond FAQ!</span>
            </div>

            {/* Main Heading */}
            <div className="space-y-2 sm:space-y-6">
              <h2 className="text-[24px] lg:text-5xl text-[#252525] dark:text-white leading-tight">
                Everything You Need to Know About Our <span className="inline">Speakers</span>
              </h2>
            </div>
          </div>

          {/* Right Column - FAQ Accordion */}
          <div className="space-y-4 mt-8 lg:mt-0">
            <div className="min-h-[400px] sm:min-h-[500px]">
              <Accordion type="single" collapsible={false} defaultValue="item-1" className="w-full">
              {faqs.map((faq) => (
                <AccordionItem
                  key={faq.id}
                  value={faq.id}
                  className="border-b border-[#ecedef] dark:border-gray-700/50"
                >
                  <AccordionTrigger className="lg:text-[28px] text-[24px] font-light w-full text-[#252525] dark:text-[#fafafa] hover:text-[#252525]/80 dark:hover:text-[#fafafa]/80 py-4 sm:py-6 md:py-8 [&[data-state=open]>svg]:rotate-180 hover:no-underline">
                    {faq.question}
                  </AccordionTrigger>
                  <AccordionContent className="text-[#252525] dark:text-[#fafafa] pb-4 sm:pb-6 text-[16px] leading-relaxed">
                    <div className="max-w-none pr-4">
                      {faq.answer}
                    </div>
                  </AccordionContent>
                </AccordionItem>
              ))}
              </Accordion>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { useState, useEffect } from "react";
import Image from "next/image";
import Link from "next/link";
import { Paperclip } from "lucide-react";
import { blogPosts } from "@/data/blog";

export function BlogSection() {
  const [hoveredCard, setHoveredCard] = useState<number | null>(null);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    checkMobile();
    window.addEventListener('resize', checkMobile);

    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  // Use only the first 3 blog posts for the section
  const displayedPosts = blogPosts.slice(0, 3);

  const defaultActiveCard = 1; // Second card (index 1) is active by default

  return (
    <section className="px-4 sm:px-6 md:px-8 pb-12 sm:pb-16 md:pb-18 md:py-10 py-12">
      <div className="max-w-7xl mx-auto">

        {/* Header Section */}
        <div className="mb-8 sm:mb-10 md:mb-12">
          {/* Badge */}
          <div className="flex border-[0.5px] border-[#2525252f] dark:border-[#fafafa4b] rounded-full px-4 py-2 w-fit items-center justify-center gap-2 mb-6 sm:mb-8">
            <Paperclip className="w-4 h-4 sm:w-5 sm:h-5 text-[#252525] dark:text-[#fafafa]" />
            <span className="text-base sm:text-lg md:text-[20px] text-[#252525] dark:text-[#fafafa]">
              Blog
            </span>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-12 justify-between items-start lg:items-center">
            {/* Main Title */}
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] text-[#252525] dark:text-[#fafafa] leading-tight">
              Sound Insights
            </h2>

            {/* Description */}
            <p className="text-[20px] text-[#252525] lg:text-right dark:text-[#fafafa] leading-tight">
              Stay updated with the latest trends in audio technology, speaker innovations, and expert tips to enhance your listening experience.
            </p>
          </div>
        </div>

        {/* Blog Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6 sm:gap-8 md:gap-10">
          {displayedPosts.map((post, index) => (
            <Link
              key={post.id}
              href={`/blog/${post.slug}`}
              className="group block"
              onMouseEnter={() => setHoveredCard(index)}
              onMouseLeave={() => setHoveredCard(null)}
            >
              <article className={`transition-all duration-500 ease-out rounded-2xl sm:rounded-3xl overflow-hidden ${
                isMobile || (hoveredCard === null && index === defaultActiveCard) || hoveredCard === index
                  ? 'bg-[#fafafa] dark:bg-[#2a2a2a]'
                  : 'bg-transparent'
              }`}>

                {/* Image */}
                <div className="relative h-[200px] sm:h-[250px] md:h-[280px] lg:h-[320px] overflow-hidden rounded-2xl sm:rounded-3xl">
                  <Image
                    src={post.image}
                    alt={post.title}
                    width={400}
                    height={320}
                    className="w-full h-full object-cover transition-transform duration-500 ease-out group-hover:scale-105"
                  />
                </div>

                {/* Content */}
                <div className={`p-4 sm:p-6 transition-all duration-500 ease-out ${
                  !isMobile && hoveredCard === index
                    ? 'transform translate-x-1 sm:translate-x-2'
                    : !isMobile && hoveredCard !== null && (
                        index === hoveredCard - 1 ||
                        index === hoveredCard + 1
                      )
                      ? 'transform -translate-x-0.5 sm:-translate-x-1'
                      : 'transform translate-x-0'
                }`}>

                  {/* Title */}
                  <h3 className="text-[24px] text-[#252525] dark:text-[#fafafa] mb-1 ">
                    {post.title}
                  </h3>

                  {/* Date */}
                  <p className="text-[#252525] text-[16px] dark:text-[#fafafa] opacity-70 mb-4">
                    {post.date}
                  </p>

                </div>

              </article>
            </Link>
          ))}
        </div>

     

      </div>
    </section>
  );
}

"use client"

import { useState, useRef, useEffect } from 'react'
import { motion } from 'framer-motion'

/**
 * AnimatedButton Component
 * 
 * A reusable button component with animated border effect
 * 
 * @param {Object} props
 * @param {React.ReactNode} props.children - Button content
 * @param {string} props.className - Additional CSS classes for the button
 * @param {Function} props.onClick - Click handler
 * @param {boolean} props.disabled - Disabled state
 * @param {Object} props.style - Inline styles
 * @param {string} props.containerClassName - Additional CSS classes for the container
 */
export function AnimatedButton({ 
  children, 
  className = "", 
  onClick,
  disabled = false,
  style = {},
  containerClassName = "",
  ...props 
}: {
  children: React.ReactNode;
  className?: string;
  onClick?: () => void;
  disabled?: boolean;
  style?: React.CSSProperties;
  containerClassName?: string;
  [key: string]: unknown;
}) {
  const [isHovered, setIsHovered] = useState(false)
  const [dimensions, setDimensions] = useState({ width: 0, height: 0 })
  const buttonRef = useRef<HTMLButtonElement>(null)

  useEffect(() => {
    if (buttonRef.current) {
      const { width, height } = buttonRef.current.getBoundingClientRect()
      setDimensions({ width, height })
    }
  }, [children])

  const createPath = () => {
    const w = dimensions.width + 16
    const h = dimensions.height + 16
    const inset = 4
    const radius = (h - (inset * 2)) / 2 + 4

    return `
      M ${inset + radius} ${inset}
      L ${w - inset - radius} ${inset}
      Q ${w - inset} ${inset} ${w - inset} ${inset + radius}
      L ${w - inset} ${h - inset - radius}
      Q ${w - inset} ${h - inset} ${w - inset - radius} ${h - inset}
      L ${inset + radius} ${h - inset}
      Q ${inset} ${h - inset} ${inset} ${h - inset - radius}
      L ${inset} ${inset + radius}
      Q ${inset} ${inset} ${inset + radius} ${inset}
    `
  }

  return (
    <div className={`relative inline-block p-2 ${containerClassName}`}>
      <button
        ref={buttonRef}
        className={`relative z-10 ${className}`}
        style={style}
        onClick={onClick}
        disabled={disabled}
        onMouseEnter={() => !disabled && setIsHovered(true)}
        onMouseLeave={() => !disabled && setIsHovered(false)}
        {...props}
      >
        {children}
      </button>

      {/* Animated SVG Border */}
      {dimensions.width > 0 && !disabled && (
        <svg
          className="absolute pointer-events-none"
          style={{
            top: '0px',
            left: '0px',
            width: dimensions.width + 16,
            height: dimensions.height + 16
          }}
          viewBox={`0 0 ${dimensions.width + 16} ${dimensions.height + 16}`}
          fill="none"
        >
          <motion.path
            d={createPath()}
            stroke="#252525"
            className="dark:stroke-[#FAFAFA]"
            strokeWidth="2"
            fill="none"
            initial={{ pathLength: 0 }}
            animate={{ pathLength: isHovered ? 1 : 0 }}
            transition={{ duration: 0.5, ease: "easeInOut" }}
          />
        </svg>
      )}
    </div>
  )
}
