"use client"

import { useState } from "react"
import { MessageSquare, ChevronLeft, ChevronRight } from "lucide-react"

export function GallerySection() {
  const [activeImage, setActiveImage] = useState(0)

  const galleryImages = [
    {
      id: 1,
      src: "/gallery-1.webp",
      alt: "Modern Black Speaker",
    },
    {
      id: 2,
      src: "/gallery-2.webp",
      alt: "Premium Audio System",
    },
    {
      id: 3,
      src: "/gallery-3.webp",
      alt: "Wireless Speaker Setup",
    },
    {
      id: 4,
      src: "/blog-1.webp",
      alt: "Studio Monitor",
    },
    {
      id: 5,
      src: "/blog-5.webp",
      alt: "Compact Speaker Design",
    },
  ]

  const handlePrevious = () => {
    setActiveImage((prev) => (prev === 0 ? galleryImages.length - 1 : prev - 1))
  }

  const handleNext = () => {
    setActiveImage((prev) => (prev === galleryImages.length - 1 ? 0 : prev + 1))
  }

  return (
    <section className="py-12 sm:py-16 md:py-20 lg:py-24 px-4 sm:px-6 md:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8 sm:mb-10 md:mb-12">
          <div className="flex border-[0.5px] border-[#2525252f] dark:border-[#fafafa4b] rounded-full px-4 py-2 w-fit items-center justify-center gap-2 mb-6 sm:mb-8">
            <MessageSquare className="w-4 h-4 sm:w-5 sm:h-5 text-[#252525] dark:text-[#fafafa]" />
            <span className="text-base sm:text-lg md:text-[20px] text-[#252525] dark:text-[#fafafa]">
              In reality
            </span>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-12 justify-between items-start lg:items-center">
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] text-[#252525] dark:text-[#fafafa] leading-tight">
              Timeless Elegance
            </h2>

            <p className="text-[20px] text-[#252525] lg:text-right dark:text-[#fafafa] leading-tight">
              Enhance your space with a perfect blend of modern design and warm ambiance. Our speaker brings style and sophistication
            </p>
          </div>
        </div>

        <div className="relative h-[300px] sm:h-[400px] md:h-[500px] lg:h-[600px]">
          <div className="relative w-full h-full rounded-2xl sm:rounded-3xl overflow-hidden bg-gray-100 dark:bg-gray-800">
            <img
              src={galleryImages[activeImage].src}
              alt={galleryImages[activeImage].alt}
              className="absolute inset-0 w-full h-full object-cover transition-opacity duration-500"
            />

            <div className="absolute left-3 sm:left-4 md:left-6 top-1/2 transform -translate-y-1/2 flex flex-col gap-2 sm:gap-3 z-20">
              {galleryImages.map((image, index) => (
                <div
                  key={image.id}
                  className={`relative cursor-pointer overflow-hidden w-[50px] h-[35px] sm:w-[60px] sm:h-[40px] rounded-[50px] transition-all duration-200 ${
                    activeImage === index ? "ring-2 ring-white scale-110" : "hover:scale-105"
                  }`}
                  onClick={() => setActiveImage(index)}
                >
                  <img
                    src={image.src}
                    alt={image.alt}
                    className="w-full h-full object-cover"
                  />
                  {activeImage === index && (
                    <div className="absolute inset-0 bg-white/20 backdrop-blur-[1px] rounded-[50px]" />
                  )}
                </div>
              ))}
            </div>

            <button
              onClick={handlePrevious}
              className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/80 dark:bg-black/80 p-2 rounded-full hover:bg-white dark:hover:bg-black transition-colors z-10"
              aria-label="Previous image"
            >
              <ChevronLeft className="w-6 h-6 text-[#252525] dark:text-[#fafafa]" />
            </button>

            <button
              onClick={handleNext}
              className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/80 dark:bg-black/80 p-2 rounded-full hover:bg-white dark:hover:bg-black transition-colors z-10"
              aria-label="Next image"
            >
              <ChevronRight className="w-6 h-6 text-[#252525] dark:text-[#fafafa]" />
            </button>
          </div>
        </div>
      </div>
    </section>
  )
}
