            <p className="text-[20px] text-[#252525] dark:text-[#fafafa] leading-relaxed">
              Designed for modern living life-upward stands premium sound with intuitive controls.
            </p>

            {/* CTA Button */}
            <AnimatedButton

            className="bg-[#252525] text-sm sm:text-base dark:bg-[#fafafa] text-[#fafafa] dark:text-[#252525] px-4 sm:px-6 py-3 sm:py-4 rounded-full font-medium flex items-center gap-2 group w-fit">
              Buy now
              <ChevronRight className="w-4 h-4 transition-transform duration-200 group-hover:translate-x-1" />
            </AnimatedButton>
          </div>
        </div>

      </div>
    </section>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { FileText, LucideIcon, Zap, Lightbulb, Radio, Ruler, Scale } from "lucide-react";
import Image from "next/image";
import { SpecificationSection, ProductImage } from "@/types";

interface ProductSpecificationsProps {
  title?: string;
  subtitle?: string;
  productImage?: ProductImage;
  specifications: SpecificationSection[];
  className?: string;
}

export default function ProductSpecifications({
  title = "Product Details",
  subtitle = "Behind every field is smart engineering—dive into the full list of features that power your experience.",
  productImage,
  specifications,
  className,
}: ProductSpecificationsProps) {
  // Icon mapping
  const iconMap: Record<string, LucideIcon> = {
    Zap,
    Lightbulb,
    Radio,
    Ruler,
    Scale,
  };

  // Helper function to get icon component
  const getIcon = (iconName: string): LucideIcon => {
    return iconMap[iconName] || FileText;
  };
  return (
    <section className={cn("py-8 md:py-20 px-4 sm:px-6 md:px-8 mt-8 md:mt-18 text-[#252525] dark:text-white", className)}>
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <div className="space-y-6 sm:space-y-8">
          {/* Badge */}
          <div className="flex items-center gap-2 sm:gap-3 text-[#252525] dark:text-white/80 w-fit border-[0.5px] border-[#25252518] dark:border-[#fafafa10] rounded-full px-3 sm:px-4 py-2 sm:py-3">
            <FileText className="w-4 h-4 sm:w-5 sm:h-5 md:w-6 md:h-6" />
            <span className="text-base sm:text-lg">Specifications</span>
          </div>

          {/* Mobile Title and Subtitle */}
          <div className="lg:hidden space-y-6 sm:space-y-8">
            <h2 className="text-2xl sm:text-3xl md:text-4xl font-medium text-[#252525] dark:text-white">
              {title}
            </h2>
            <p className="text-lg sm:text-xl text-[#252525] dark:text-[#fafafa] leading-relaxed">
              {subtitle}
            </p>
          </div>

          {/* Main Content Layout */}
          <div className="flex flex-col lg:flex-row gap-8 lg:gap-12 items-start">

            {/* Left Side - Title and Product Image (Desktop Only Title) */}
            <div className="w-full lg:w-[55%]">
              {/* Desktop Title */}
              <div className="hidden lg:block">
                <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] mb-6 sm:mb-8 md:mb-10 lg:mb-14 font-medium text-[#252525] dark:text-white">
                  {title}
                </h2>
              </div>

              {productImage && (
                <div className="relative w-full rounded-xl sm:rounded-2xl overflow-hidden">
                  <Image
                    src={productImage.src}
                    alt={productImage.alt}
                    width={800}
                    height={600}
                    className="w-full h-full object-contain"
                  />
                </div>
              )}
            </div>

            {/* Right Side - Subtitle and Specifications */}
            <div className="w-full  space-y-6 sm:space-y-8">
              {/* Desktop Subtitle */}
              <div className="hidden lg:block">
                <p className="text-lg sm:text-xl lg:text-[20px] text-[#252525] dark:text-[#fafafa] leading-relaxed lg:text-right">
                  {subtitle}
                </p>
              </div>

              {/* Specifications Sections */}
              <div className="space-y-8 sm:space-y-12 lg:space-y-16">
                {specifications.map((section, sectionIndex) => (
                  <div key={sectionIndex} className="space-y-4 sm:space-y-6 dark:bg-[#2a2a2a] bg-[#fafafa] rounded-xl sm:rounded-2xl">
                    {/* Section Title */}
                    <h3 className="text-xl font-semibold text-[#252525] dark:text-white bg-[#ecedef] dark:bg-[#2e3138] rounded-lg px-4 sm:px-6 md:px-7 py-2 sm:py-3">
                      {section.title}
                    </h3>

                    {/* Specification Items */}
                    <div className="space-y-3 sm:space-y-4 pb-4 sm:pb-6 md:pb-8">
                      {section.items.map((item, itemIndex) => (
                        <div
                          key={itemIndex}
                          className="flex flex-col sm:flex-row sm:items-center text-[20px] px-4 sm:px-6 md:px-7 py-2 border-b dark:border-gray-700/50 border-[#ecedef] gap-3"
                        >
                          {/* Label with Icon */}
                          <div className="flex items-center gap-3 flex-1">
                            {item.icon && (
                              <span className="w-5 h-5 sm:w-6 sm:h-6 text-[#252525] dark:text-[#fafafa] flex-shrink-0">
                                {(() => {
                                  const IconComponent = getIcon(item.icon);
                                  return <IconComponent className="w-5 h-5 sm:w-6 sm:h-6" />;
                                })()}
                              </span>
                            )}
                            <span className="text-[#252525] dark:text-[#fafafa]">{item.label}</span>
                          </div>

                          {/* Value */}
                          <span className="text-[#252525] dark:text-white sm:text-right font-medium sm:font-normal ml-0">
                            {item.value}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { useState, useEffect } from "react";
import Image from "next/image";
import { MessageSquare } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export function GallerySection() {
  const [activeImage, setActiveImage] = useState(0);
  const [hoveredThumbnail, setHoveredThumbnail] = useState<number | null>(null);
  const [direction, setDirection] = useState(0);
  const [animationDuration] = useState(0.6);
  const [isDesktop, setIsDesktop] = useState(false);

  // Handle responsive spacing
  useEffect(() => {
    const checkScreenSize = () => {
      setIsDesktop(window.innerWidth >= 1024);
    };

    checkScreenSize();
    window.addEventListener('resize', checkScreenSize);

    return () => window.removeEventListener('resize', checkScreenSize);
  }, []);

  const handleImageChange = (newIndex: number) => {
    if (newIndex === activeImage) return;
    
    // Calculate distance between current and target image
    const distance = Math.abs(newIndex - activeImage);
    
    // Set direction: 1 for forward (down to up), -1 for backward (up to down)
    setDirection(newIndex > activeImage ? 1 : -1);
    setActiveImage(newIndex);
    
    // Return distance for dynamic duration
    return distance;
  };

  const galleryImages = [
    {
      id: 1,
      src: "/gallery-1.webp",
      alt: "Modern Black Speaker",
    },
    {
      id: 2,
      src: "/gallery-2.webp",
      alt: "Premium Audio System",
    },
    {
      id: 3,
      src: "/gallery-3.webp",
      alt: "Wireless Speaker Setup",
    },
    {
      id: 4,
      src: "/blog-1.webp",
      alt: "Studio Monitor",
    },
    {
      id: 5,
      src: "/blog-5.webp",
      alt: "Compact Speaker Design",
    },
  ];

  // Animation variants for vertical sliding
  const slideVariants = {
    enter: (direction: number) => ({
      y: direction > 0 ? '100%' : '-100%',
      opacity: 1,
    }),
    center: {
      y: 0,
      opacity: 1,
    },
    exit: (direction: number) => ({
      y: direction > 0 ? '-100%' : '100%',
      opacity: 1,
    }),
  };

  return (
    <section className="py-12 sm:py-16 md:py-20 lg:py-24 px-4 sm:px-6 md:px-8">
      <div className="max-w-7xl mx-auto">

        {/* Header Section */}
        <div className="mb-8 sm:mb-10 md:mb-12">
          {/* Badge */}
          <div className="flex border-[0.5px] border-[#2525252f] dark:border-[#fafafa4b] rounded-full px-4 py-2 w-fit items-center justify-center gap-2 mb-6 sm:mb-8">
            <MessageSquare className="w-4 h-4 sm:w-5 sm:h-5 text-[#252525] dark:text-[#fafafa]" />
            <span className="text-base sm:text-lg md:text-[20px] text-[#252525] dark:text-[#fafafa]">
              In reality
            </span>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 lg:gap-12 justify-between items-start lg:items-center">
            {/* Main Title */}
            <h2 className="text-2xl sm:text-3xl md:text-4xl lg:text-[44px] text-[#252525] dark:text-[#fafafa] leading-tight">
              Timeless Elegance
            </h2>

            {/* Description */}
            <p className="text-[20px] text-[#252525] lg:text-right dark:text-[#fafafa] leading-tight">
              Enhance your space with a perfect blend of modern design and warm ambiance. Our speaker brings style and sophistication
            </p>
          </div>
        </div>

        {/* Gallery Layout */}
        <div className="relative h-[300px] sm:h-[400px] md:h-[500px] lg:h-[600px]">

          {/* Main Image Container */}
          <div className="relative w-full h-full rounded-2xl sm:rounded-3xl overflow-hidden bg-gray-100 dark:bg-gray-800">

            {/* Animated Images */}
            <AnimatePresence initial={false} custom={direction}>
              <motion.div
                key={activeImage}
                custom={direction}
                variants={slideVariants}
                initial="enter"
                animate="center"
                exit="exit"
                transition={{
                  type: "tween",
                  ease: [0.25, 0.1, 0.25, 1],
                  duration: 0.6
                }}
                className="absolute inset-0"
              >
                <Image
                  src={galleryImages[activeImage].src}
                  alt={galleryImages[activeImage].alt}
                  fill
                  className="object-cover"
                  priority
                />
              </motion.div>
            </AnimatePresence>

            {/* Thumbnail Overlay */}
            <div className="absolute left-3 sm:left-4 md:left-6 top-1/2 transform -translate-y-1/2 flex flex-col gap-2 sm:gap-3 z-20">

              {/* Animated Ring that follows hover */}
              <motion.div
                className="absolute ring-1 p-2 sm:p-3 ring-white shadow-lg pointer-events-none w-[50px] h-[35px] sm:w-[60px] sm:h-[40px] rounded-[50px]"
                animate={{
                  y: (hoveredThumbnail !== null ? hoveredThumbnail : activeImage) * (isDesktop ? 52 : 43), // 43 on mobile, 52 on desktop
                }}
                transition={{
                  type: "spring",
                  stiffness: 400,
                  damping: 30,
                  mass: 0.8
                }}
              />

              {galleryImages.map((image, index) => (
                <motion.div
                  key={image.id}
                  className="relative cursor-pointer overflow-hidden w-[50px] h-[35px] sm:w-[60px] sm:h-[40px] rounded-[50px]"
                  whileHover={{
                    scale: 1,
                    transition: { duration: 0.2 }
                  }}
                  whileTap={{ scale: 0.95 }}
                  animate={{
                    scale: hoveredThumbnail === index ? 1.2 :
                           hoveredThumbnail !== null ? 1.1 : 1.0
                  }}
                  transition={{ duration: 0.3, ease: "easeOut" }}
                  onClick={() => handleImageChange(index)}
                  onMouseEnter={() => setHoveredThumbnail(index)}
                  onMouseLeave={() => setHoveredThumbnail(null)}
                >
                  <Image
                    src={image.src}
                    alt={image.alt}
                    fill
                    className="object-cover"
                  />

                  {/* Active indicator overlay - only show on actual active image */}
                  {activeImage === index && hoveredThumbnail === null && (
                    <motion.div
                      className="absolute inset-0 bg-white/10 backdrop-blur-[1px] rounded-[50px]"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    />
                  )}


                </motion.div>
              ))}
            </div>

            {/* Overlay gradient for better contrast */}
            <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 hover:opacity-100 transition-opacity duration-300 z-10" />
          </div>

        </div>

      </div>
    </section>
  );
}
